edit.find:
$sql = 'UPDATE ' . GALLERY_ALBUMS_TABLE . "
	SET album_name = '" . $db->sql_escape($new_name) . "'
	WHERE album_name = '" . $db->sql_escape($old_name) . "'
		AND album_user_id != 0
		AND parent_id = 0";
$db->sql_query($sql);

edit.action:
$sql = 'UPDATE ' . GALLERY_ALBUMS_TABLE . "
	SET album_name = '" . $db->sql_escape($new_name) . "'
	WHERE album_name = '" . $db->sql_escape($old_name) . "'
		AND album_user_id <> 0
		AND parent_id = 0";
$db->sql_query($sql);
